const button=document.querySelector(".button"),navLinks=document.querySelector(".nav-links"),topHeader=document.querySelector("nav .title"),navLinksLi=document.querySelectorAll(".nav-links li");button.addEventListener("click",e=>{e.preventDefault(),navLinks.classList.toggle("pressed"),topHeader.classList.toggle("border-remove"),navLinks.style.animation=".5s ease-out forwards navExtend",navLinksLi.forEach(function(e){e.style.animation="1s ease-out forwards navLinksAppear"})});